import React from 'react';
import ReactDOM from 'react-dom';

const Header = ( props ) => {
    return (
        <div>
            <h1>{props.course}</h1>
        </div>
    )
}

const Part = ( props ) => {
    return (
        <div>
            <p>{props.content} {props.kent}</p>
        </div>
    )

} 


const Content = ( props ) => {
    console.log(props)
    return (
        <div>
            <Part content={props.parts[0].name} kent={props.parts[0].exercises}/>
            <Part content={props.parts[1].name} kent={props.parts[1].exercises}/>
            <Part content={props.parts[2].name} kent={props.parts[2].exercises}/>
        </div>
    )
}

const Total = ( props ) => {
    console.log(props)
    return (
        <div>
            <p>Number of exercises {props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises}</p>
        </div>
    )
}

const App = () => {
    const course = {
        name: 'Half Stack application development',
        parts: [
            {
                name: 'Fundamentals of React',
                exercises: 10,
                id: 1
            },
            {
                name: 'Using props to pass data',
                exercises: 7,
                id: 2
            },
            {
                name: 'State of a component',
                exercises: 14,
                id: 3
            }
        ]
    }

    return (
        <div>
            <Header course={course.name} />
            <Content parts={course.parts}/>
            <Total parts={course.parts}/>
        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));

