import React from 'react';
import ReactDOM from 'react-dom';

const Header = ( props ) => {
    return (
        <div>
            <h1>{props.course}</h1>
        </div>
    )
}

const Part = ( props ) => {
    return (
        <div>
            <p>{props.content} {props.kent}</p>
        </div>
    )

} 


const Content = ( props ) => {
    console.log(props)
    return (
        <div>
            <Part content={props.name} kent={props.exercises}/>
            <Part content={props.content2} kent={props.kent2}/>
            <Part content={props.content3} kent={props.kent3}/>
        </div>
    )
}

const Total = ( props ) => {
    console.log(props)
    return (
        <div>
            <p>Number of exercises {props.tht1 + props.tht2 + props.tht3}</p>
        </div>
    )
}

const App = () => {
    const course = 'Half Stack application development'
    const parts = [
        {
            name: 'Fundamentals of React',
            exercises: 10
        },
        {
            name: 'Using props to pass data',
            exercises: 7
        },
        {
            name: 'State of a component',
            exercises: 14
        }
    ]


    return (
        <div>
            <Header course={course} />
            <Content parts={parts}/>
            <Total parts={parts}/>
        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));

