import React from 'react';
import ReactDOM from 'react-dom';

const Header = ( props ) => {
    return (
        <div>
            <h1>{props.course}</h1>
        </div>
    )
}

const Part = ( text, nr ) => {
    return (
        <div>
            <p>{text} {nr}</p>
        </div>
    )

} 


const Course = ( props ) => {
console.log(props)
    return (
        <div>
            <Part text={props.parts[0].name} nr={props.parts[0].exercises} />
            <Part text={props.parts[1].name} nr={props.parts[1].exercises} />
            <Part text={props.parts[2].name} nr={props.parts[2].exercises} />
        </div>
    )
}

const Total = ( props ) => {
    return (
        <div>
            <p>Number of exercises {props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises}</p>
        </div>
    )
}

const App = () => {
    const course = {
        name: 'Half Stack application development',
        parts: [
            {
                name: 'Fundamentals of React',
                exercises: 10
            },
            {
                name: 'Using props to pass data',
                exercises: 7
            },
            {
                name: 'State of a component',
                exercises: 14
            }
        ]
    }

    return (
        <div>
            <Header course={course.name} />
            <Course course={course.parts}/>
            {console.log('course parts ', course.parts)}
            <Total parts={course.parts}/>
        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));

