import React from 'react';
import ReactDOM from 'react-dom';


const Header = ( props ) => { //{ const courseName = course.name} --> liitet채채n propsiin --> props.courseName 
    const courseName = props.courseName

    return (
        <div>
            <h1>{courseName}</h1>
        </div>
    )
}

const Part = ( {text, nr} ) => {
    return (
        <div>
            <p>{text} {nr}</p>
        </div>
    )
} 

// <Course courseParts={course.parts}/> // [osa1, osa2, osa3]  courseParts is list of objects
const Course = ({ courseParts }) => {
    console.log(courseParts) // --> { course: [part1jotaintain, part2jotaintain] }
    return (
        <div>
            <Part text={courseParts[0].name} nr={courseParts[0].exercises} />
            <Part text={courseParts[1].name} nr={courseParts[1].exercises} />
            <Part text={courseParts[2].name} nr={courseParts[2].exercises} />
        </div>
    )
}

const Total = ( props ) => {
    return (
        <div>
            <p>Number of exercises {props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises}</p>
        </div>
    )
}

const App = () => {
    const course = {
        name: 'Half Stack application development',
        parts: [
            {
                name: 'Fundamentals of React',
                exercises: 10
            },
            {
                name: 'Using props to pass data',
                exercises: 7
            },
            {
                name: 'State of a component',
                exercises: 14
            }
        ]
    }

    return (
        <div>
            <Header courseName={course.name} />
            <Course courseParts={course.parts}/>
            <Total parts={course.parts}/>
        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));

