import React from 'react';
import ReactDOM from 'react-dom';

const Header = ( props ) => {
    return (
        <div>
            <h1>{props.course}</h1>
        </div>
    )
}

const Part = ( props ) => {
    return (
        <div>
            <p>{props.content1} {props.kent1}</p>
        </div>
    )

} 


const Content = ( props ) => {
    console.log(props)
    return (
        <div>
            <Part />
        </div>
    )
}

const Total = ( props ) => {
    console.log(props)
    return (
        <div>
            <p>Number of exercises {props.tht1 + props.tht2 + props.tht3}</p>
        </div>
    )
}

const App = () => {
    const course = 'Half Stack application development'
    const part1 = 'Fundamentals of React'
    const part2 = 'Using props to pass data'
    const part3 = 'State of a component'
    const exercises1 = 10
    const exercises2 = 7
    const exercises3 = 14



    return (
        <div>
      <Header course={course} />
      <Content 
        content1={part1} 
        content2={part2} 
        content3={part3}
        kent1={exercises1}
        kent2={exercises2}
        kent3={exercises3}
        />
      <Total 
        tht1={exercises1}
        tht2={exercises2}
        tht3={exercises3}
      />
    </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));

